<?php class User_List {				private $con;				public function __construct() {			$this->con = mysqli_connect("localhost", "root", "", "todolist");		}		public function closeDB() {			if(isset($this->con)){				mysqli_close($this->con);			}		}		public function displayList() {			$user_id = $_SESSION['user_id'];			$sql = "SELECT * 					FROM list					WHERE user_id=$user_id					ORDER BY str_pos DESC";			$result = mysqli_query($this->con, $sql);			echo '<ul id="list">';			while($row = mysqli_fetch_array($result)) {				echo '<li id="' . $row['id'] . '" rel="' . $user_id . '">' . $row['list_item'] . '<span class="done"> done</span>' . '</li>';			}			echo '</ul>';		}		public function addList() {			$user_id = mysqli_real_escape_string(htmlspecialchars($_POST['userId']));			$new_list = mysqli_real_escape_string(htmlspecialchars($_POST['newList']));			$str_pos = mysqli_real_escape_string(htmlspecialchars($_POST['listPos']));			$sql = "INSERT INTO list ( user_id, list_item, str_pos) values('$user_id', '$new_list', '$str_pos')";			$insert = mysqli_query($this->con, $sql);			$id = mysqli_insert_id($this->con);			$return['id'] = $id;			$return['userId'] = $user_id;			echo json_encode($return);			mysqli_close($this->con);		}		public function deleteList() {			$user_id = $_POST['userId'];			$id = $_POST['id'];			$sql = "DElETE FROM list WHERE user_id=$user_id AND id=$id";			$delete = mysqli_query($this->con, $sql);			mysqli_close($this->con);		}}?>